package com.syntax.class01;

public class VariableValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Create a Java program and name it Variable Values.
		 * In your program, declare variables using different ways to store all different primitive data.
		 * And then print value of all variables
		 */
		
		byte a = 123;
		int b = 1234567890;
		short c = 12345;
		long d = 1234567890L;
		
		float e = 1.234567890F;
		double f = 123.4567890;
		
		char ch1 = 'A';
		char ch2 = 'B';
		
		boolean g = true;
		boolean h = false;
		
		System.out.println("The byte value is " + a);
		System.out.println("The integer value is " + b);
		System.out.println("The short value is " + c);
		System.out.println("The long value is " + d);
		System.out.println("The float value is " + e);
		System.out.println("The double value is " + f);
		System.out.println("The first character value is " + ch1);
		System.out.println("The second charcter value is " + ch2);
		System.out.println("Crypto is the next King - " + g);
		System.out.println("Crypto is not the next King - " + h);		

	}

}
