package com.syntax.class02;

public class HW1_ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 *  Write a Java program to add, subtract, multiply and divide 2 decimal values.
		 *  Your program should say. "The ___ of 2 numbers _ and _ is equal to ___."
		 */


		double a=456.654;
		double b=123.321;
		
		double addition=a+b;
		double subtraction=a-b;
		double multiplication=a*b;
		double division=a/b;
		
		System.out.println("The addition of 2 numbers " +a +" and " +b +" is equals to " +addition +"."  );
		System.out.println("The subtraction of 2 numbers " +a +" and " +b +" is equals to " +subtraction +"."  );
		System.out.println("The multiplication of 2 numbers " +a +" and " +b +" is equals to " +multiplication +"."  );
		System.out.println("The division of 2 numbers " +a +" and " +b +" is equals to " +division +"."  );
		
		
		
	}

}
