package com.syntax.class02;

public class HW3_AreaAndPerimeter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Write a program to print the area and perimeter of a rectangle with width = 5 and  height = 8.
		 * Your program should say. "The perimeter of a rectangle with width _ and height __ is equal to __ 
		 * and the area is _."
		 */
				
				int width=5;
				int height=8;
				
				int perimeter=2*(width+height);
				int area = width*height;
				
				System.out.println("The perimeter of a rectangle with width " +width 
						+ " and height " +height +" is equal to " +perimeter +" and the area is equal to " +area +".");

			}

		}


	
