package com.syntax.class02;

public class VariableCreation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// First way to create a variable.

		byte b=10; // Create a variable and assign a value
		
		// Second way to create a variable.
		
		int i; // Create a variable
		i = 100; // Assign a value
		
		/* We need to create a variable only once and 
		then we can use as many times as we want/need */
		
		// Third way to create or declare a variable.
		
		int num1, num2, num3;
		
		        num1=200; // Assign value or initialize a variable
				num2=300;
				num3=400;
				
				num2=3000; // Reassigning a value
				
				// One variable can hold only one value at a time
				
				System.out.println(b);
				System.out.println(i);
				System.out.println(num1);
				System.out.println(num2);
				System.out.println(num3);
		
	}

}
