package com.syntax.class03;

public class HW1_DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		/* 
		 * Create a Java program and name it DoubleComparison. Declare 2 double values
		 * and if value of first variable is higher than the second,
		 * print "Double value _ is larger than _ ." Otherwise print...
		 * */
		

		double a=15.1;
		double b=10.2;
		
		if(a>b)
		{
			System.out.println("Double value " +a +" is greater than " +"double value " +b +".");
		}
		else
		{
			System.out.println("Double value " +a +" is smaller than " +"double value " +b +".");
		}
	}

}
