package com.syntax.reviewclass04;

public class ForLoopDemo4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int j=0;j<5;j++)
		{
			for(int i=0;i<3;i++)
			{
				System.out.println(i);
			}
			
			System.out.println();
			System.out.println("**************");
			System.out.println();
			
			for (int a=0;a<5;a++)
			{
				int b=0;
				while(b<3)
				{
					System.out.println(b);
					b++;
				}
				}
			}
		}
		
	}


